                        <span className={`${(isDarkMode) ? "text-light" : "text-dark"}`}>Password</span>
                        <i className={`${(isDarkMode) ? "bg-light" : "bg-dark "}`}></i>
                    </div>
                    <div className="inputbox mb-3 mb-md-0 col-lg-3 col-12">
