                    <h2 className={`col-lg-6 col-12 ${(window.innerWidth > 1100) ? SmallFontSize + ' p-2' : "fs-6 p-1"}  text-lg-start text-center ${(isDarkMode) ? " text-light" : " text-dark"} rounded-4`} >Font Size :</h2>
                    <div className={`col-lg-6 col-12 d-flex justify-content-lg-end justify-content-center p-0 m-0`}>
                        <select className={`form-select  ${(isDarkMode) ? "text-light" : "text-dark"}`} aria-label="Default select example" value={FontSize} onChange={handleFontSizeChange}>
                            <option value="fs-1">Small</option>
