import React from 'react';
import { Line } from 'react-chartjs-2'; // Import Line from 'react-chartjs-2'

function Services({ isDarkMode, toggleMode, FontSize, setFontSize }) {
    const data = {
        labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
        datasets: [
            {
                label: 'Sales',
                data: [65, 59, 80, 81, 56, 55, 40],
                fill: false,
                borderColor: 'rgb(75, 192, 192)',
                tension: 0.1,
            },
        ],
    };

    // Configuration options for the chart
    const options = {
        scales: {
            y: {
                beginAtZero: true,
            },
        },
    };

    return (
        <>
            <div className={`row d-flex justify-content-center m-0 px-2 pt-lg-0 pt-lg-4 pt-5 pb-4`} data-aos="zoom-in" data-aos-duration="1000">
                <h2 className={`text-light col-lg-6 col-10 ${(window.innerWidth > 1100) ? FontSize : "fs-6"}  text-center ${(isDarkMode) ? "bg-dark text-light" : "bg-light text-dark"} rounded-3 p-2`} >Services</h2>
            </div >
            <div className={`row col-12 rounded-3  justify-content-center ${(isDarkMode) ? "bg-dark " : "bg-light "} bg-opacity-50 m-0 p-lg-5 p-3`} data-aos="zoom-in" data-aos-duration="1000">
                <Line data={data} options={options} /> {/* Use Line instead of Chart */}
            </div>
        </>
    )
}

export default Services;
