import { Link, useLocation } from 'react-router-dom';
import { useMemo, useEffect } from 'react';

function Navlist(props) {
    const location = useLocation();

    const isActive = useMemo(
        () => (item) => {
            return item.to === location.pathname;
        },
        [location]
    );

    useEffect(() => {
        const handleButtonClick = (event) => {
            // Handle button click event
        };

        // Add event listener to document
        document.addEventListener('click', handleButtonClick);

        // Cleanup function
        return () => {
            // Remove event listener when component unmounts
            document.removeEventListener('click', handleButtonClick);
        };
    }, []); // Empty dependency array means this effect runs only once, like componentDidMount

    return (
        <div className="row col-12 p-3 pe-4 pt-5">
            <div className="col-12 d-flex flex-column p-0 m-0 justify-content-start">
                {props.items.map((item) => {
                    return (
                        <Link
                            to={item.to}
                            className={`${props.isDarkMode ? "text-light" : "text-dark"} col-12 text-decoration-none m-0 p-0 row`}
                        >
                            <button
                                key={item.id}
                                className={`row col-12 my-1 p-2 border-0 ${isActive(item)
                                    ? "bg-pirple text-light"
                                    : props.isDarkMode
                                        ? "bg-dark text-light"
                                        : "bg-light text-dark"
                                    } rounded-2  bg-opacity-50 navitem`}
                            >

                                <img src={item.imgsrc} alt={item.imgalt} className="col-3" />
                                <h6 className="col-9 m-0 p-0">{item.text}</h6>

                            </button>
                        </Link>
                    );
                })}
            </div>
        </div>
    );
}

export default Navlist;
